            dragIcon.alpha = 1.0f
            buttonIcon.alpha = 1.0f

            visibilitySwitch.setOnCheckedChangeListener { _, isChecked ->
                item.isVisible = isChecked
                onItemChanged(items)
            }

            dragIcon.setOnTouchListener { _, event ->
                if (event.action == MotionEvent.ACTION_DOWN) {
                    onStartDrag(this)
                    true
                } else {
                    false
                }
            }
