enum class FilterSortOrder {
    DEFAULT,
    NAME
}

private const val PREF_FILTER_SORT_ORDER = "filter_sort_order"

class OverlayService : Service() {
    private lateinit var windowManager: WindowManager
    private var overlayView: View? = null
    private var currentIndex = 0
    private val TAG = "OverlayService"
    private var viewModel: HomeViewModel? = null
    private var invasionsObserver: Observer<List<Invasion>>? = null
    private var errorObserver: Observer<String>? = null
    private lateinit var homeCoordinatesManager: HomeCoordinatesManager
    private var favoritesOverlayView: View? = null
    private var filterOverlayView: View? = null
    private var isFavoritesVisible = false
    private var isFilterVisible = false
    private lateinit var favoritesAdapter: OverlayFavoritesAdapter
    private lateinit var filtersAdapter: FiltersRecyclerView
    private lateinit var filterPreferences: FilterPreferences
    private lateinit var deletedInvasionsRepository: DeletedInvasionsRepository
    private var currentMode = "invasions" // Default mode
    private var currentSortOrder = FilterSortOrder.DEFAULT
    private var currentX = 0
    private var currentY = 100
    private lateinit var dragTouchListener: DragTouchListener
    private var currentFavoritesSortOrder: FilterSortOrder = FilterSortOrder.DEFAULT
    private val PREF_FAVORITES_SORT_ORDER = "favorites_sort_order"
    private lateinit var customizationManager: OverlayCustomizationManager
    private var customizationOverlayView: View? = null
    private var isCustomizationVisible = false
    private lateinit var customizationAdapter: OverlayCustomizationAdapter
    private var itemTouchHelper: ItemTouchHelper? = null

    companion object {
        private const val TAG = "OverlayService"
        private const val NOTIFICATION_ID = 1001
        private const val CHANNEL_ID = "overlay_service_channel"
    }

    override fun onBind(intent: Intent?): IBinder? {
        return null // Not a bound service
    }

    override fun onCreate() {
        super.onCreate()
        Log.d(TAG, "Service onCreate")
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
            val channel = NotificationChannel(
                CHANNEL_ID,
                "Overlay Service",
                NotificationManager.IMPORTANCE_LOW
            )
            channel.description = "Running the Pokemon GO invasion overlay"
            val notificationManager = getSystemService(NotificationManager::class.java)
            notificationManager.createNotificationChannel(channel)
        }
        homeCoordinatesManager = HomeCoordinatesManager.getInstance(this)
        filterPreferences = FilterPreferences(this)
        deletedInvasionsRepository = DeletedInvasionsRepository(this)
        customizationManager = OverlayCustomizationManager(this)

        val notification = NotificationCompat.Builder(this, CHANNEL_ID)
            .setContentTitle("Invasion Overlay")
            .setContentText("Overlay service is running")
            .setSmallIcon(R.drawable.ic_launcher_foreground)
            .setPriority(NotificationCompat.PRIORITY_LOW)
            .build()
        startForeground(NOTIFICATION_ID, notification)
        windowManager = getSystemService(WINDOW_SERVICE) as WindowManager
        val favorites = FavoritesManager.getFavorites(this)
        if (favorites.isNotEmpty()) {
            Log.d(TAG, "Favorites loaded: ${favorites.size} items")
        } else {
            Log.d(TAG, "No favorites found")
        }

        // Save overlay running state
        val sharedPrefs = getSharedPreferences("overlay_prefs", Context.MODE_PRIVATE)
        sharedPrefs.edit().putBoolean("overlay_running", true).apply()
    }

    // Android 15 compatibility: Handle foreground service timeout
    override fun onTimeout(startId: Int, fgsType: Int) {
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.VANILLA_ICE_CREAM) {
            Log.w(TAG, "Foreground service timeout reached for startId: $startId, fgsType: $fgsType")
            // Clean up resources and stop the service
            try {
                cleanupOverlays()
                stopSelf()
            } catch (e: Exception) {
                Log.e(TAG, "Error during timeout cleanup: ${e.message}")
                stopSelf() // Ensure we stop even if cleanup fails
            }
        }
    }

    private fun cleanupOverlays() {
        try {
            overlayView?.let {
                if (it.windowVisibility == View.VISIBLE) {
                    windowManager.removeView(it)
                }
                overlayView = null
            }
            favoritesOverlayView?.let {
                if (it.windowVisibility == View.VISIBLE) {
                    windowManager.removeView(it)
                }
                favoritesOverlayView = null
            }
            filterOverlayView?.let {
                if (it.windowVisibility == View.VISIBLE) {
                    windowManager.removeView(it)
                }
                filterOverlayView = null
            }
            cleanupObservers()
        } catch (e: Exception) {
            Log.e(TAG, "Error during overlay cleanup: ${e.message}")
        }
    }

    // Android 15 compatibility: Check if overlay is visible before starting foreground service
    private fun isOverlayVisible(): Boolean {
        return overlayView?.let { view ->
            view.windowVisibility == View.VISIBLE && view.visibility == View.VISIBLE
        } ?: false
    }

    override fun onStartCommand(intent: Intent?, flags: Int, startId: Int): Int {
        val mode = intent?.getStringExtra("mode").toString()
        Log.d(TAG, "Service onStartCommand with mode: $mode")

        // Save the current mode
        val sharedPrefs = getSharedPreferences("overlay_prefs", Context.MODE_PRIVATE)
        sharedPrefs.edit().putString("overlay_mode", mode).apply()

        // Android 15 compatibility: Ensure overlay is visible for SYSTEM_ALERT_WINDOW compliance
        if (overlayView == null) {
            addOverlay(mode)
            Log.d(TAG, "Overlay re-added on onStartCommand with mode: $mode")
        } else {
            updateOverlayBasedOnMode(mode)
            Log.d(TAG, "Overlay updated in onStartCommand with mode: $mode")
        }
        return START_NOT_STICKY
    }

    private fun addOverlay(mode: String) {
        if (overlayView != null) return
        overlayView = LayoutInflater.from(this).inflate(R.layout.overlay_layout, null)
        val params = WindowManager.LayoutParams().apply {
            width = WRAP_CONTENT
            height = WRAP_CONTENT
            type = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O)
                TYPE_APPLICATION_OVERLAY
            else
                TYPE_SYSTEM_ALERT
            flags = FLAG_NOT_TOUCH_MODAL or FLAG_NOT_FOCUSABLE or FLAG_WATCH_OUTSIDE_TOUCH
            format = PixelFormat.TRANSLUCENT
            gravity = Gravity.TOP or Gravity.START
            x = currentX
            y = currentY
        }
        try {
            windowManager.addView(overlayView, params)
            Log.d(TAG, "Overlay added successfully with mode: $mode")
        } catch (e: Exception) {
            Log.e(TAG, "Error adding overlay: ${e.message}")
            return
        }
        setupButtons(mode, params)
        if (mode == "quests") {
            if (CurrentQuestData.currentQuests.isEmpty())
                fetchQuests()
        } else {
            val invasions = CurrentInvasionData.currentInvasions
            if (invasions.isEmpty())
                fetchInvasions()
        }
    }

    private fun setupButtons(mode: String, params: WindowManager.LayoutParams) {
        val dragHandle = overlayView?.findViewById<ImageButton>(R.id.drag_handle)
        val closeBtn = overlayView?.findViewById<ImageButton>(R.id.close_button)
        val rightBtn = overlayView?.findViewById<ImageButton>(R.id.right_button)
        val leftBtn = overlayView?.findViewById<ImageButton>(R.id.left_button)
        val homeBtn = overlayView?.findViewById<ImageButton>(R.id.home_button)
        val refreshBtn = overlayView?.findViewById<ImageButton>(R.id.refresh_button)
        val switchModesBtn = overlayView?.findViewById<ImageButton>(R.id.switch_modes)
        val favoritesButton = overlayView?.findViewById<ImageButton>(R.id.favorites_tab)
        val filtersButton = overlayView?.findViewById<ImageButton>(R.id.filter_tab)

        // Create DragTouchListener without long-press callback (no longer needed)
        dragTouchListener = DragTouchListener(params, windowManager, overlayView!!)

        switchModesBtn?.setImageResource(if (mode == "quests") R.drawable.binoculars else R.drawable.team_rocket_logo)
        if (dragHandle == null || closeBtn == null || rightBtn == null || leftBtn == null
            || homeBtn == null || refreshBtn == null ||
            switchModesBtn == null || favoritesButton == null || filtersButton == null) {
            Log.e(TAG, "One or more buttons not found in layout")
            return
        }

        dragHandle.setOnTouchListener(dragTouchListener)

        // Apply saved customization settings
        applyCustomizationToOverlay()

        closeBtn.setOnClickListener {
            Log.d(TAG, "Close button clicked")
            showOverlayToast("Closing overlay")
            try {
                if (overlayView != null) {
                    windowManager.removeView(overlayView)
                    overlayView = null
                }

                stopForeground(STOP_FOREGROUND_REMOVE)
                val notificationManager = getSystemService(NOTIFICATION_SERVICE) as NotificationManager
                notificationManager.cancel(NOTIFICATION_ID)

                stopSelf()
            } catch (e: Exception) {
                Log.e(TAG, "Error removing view: ${e.message}")
            }
        }

        homeBtn.setOnClickListener {
            val coordinates = homeCoordinatesManager.getHomeCoordinates()
            if(coordinates != null) {
                val(latitude, longitude) = coordinates
                launchHome(latitude,longitude)
            } else {
                Log.d(TAG, "Home coordinates not set")
                showOverlayToast("Home coordinates not set")
            }
        }

        refreshBtn.setOnClickListener {
            if (mode == "quests") {
                Log.d(TAG, "Refresh button clicked in quests mode")
                showOverlayToast("Refreshing quests...")
                fetchQuests()
            } else {
                Log.d(TAG, "Refresh button clicked in invasions mode")
                showOverlayToast("Refreshing invasions...")
                fetchInvasions()
            }
        }

        switchModesBtn.setOnClickListener {
            if (mode == "quests") {
                Log.d(TAG, "Switching to invasions mode")
                showOverlayToast("Switching to invasions mode")
                currentX = dragTouchListener.getCurrentParamsX()
                currentY = dragTouchListener.getCurrentParamsY()
                currentMode = "invasions"

                cleanupObservers()
                if (overlayView != null) {
                    windowManager.removeView(overlayView)
                    overlayView = null
                }
                addOverlay("invasions")
            } else {
                showOverlayToast("Switching to quests mode")
                Log.d(TAG, "Switching to quests mode")
                currentX = dragTouchListener.getCurrentParamsX()
                currentY = dragTouchListener.getCurrentParamsY()
                currentMode = "quests"

                cleanupObservers()
                if (overlayView != null) {
                    windowManager.removeView(overlayView)
                    overlayView = null
                }
                addOverlay("quests")
            }
        }

        filtersButton.setOnClickListener {
            showFiltersOverlay()
        }

        favoritesButton?.setOnClickListener {
            showFavoritesOverlay()
        }

        rightBtn.setOnClickListener {
            if (mode == "quests") {
                val currentQuests = CurrentQuestData.currentQuests
                if (currentQuests.isEmpty()) {
                    Log.d(TAG, "No quests available, attempting to fetch...")
                    showOverlayToast("No quests available, fetching...")
                    fetchQuests()
                    return@setOnClickListener
                }
                currentIndex = (currentIndex + 1) % currentQuests.size
                Log.d(TAG, "Navigating to quest at index $currentIndex: ${currentQuests[currentIndex].lat}, ${currentQuests[currentIndex].lng}")
                showOverlayToast( "Teleporting to ${currentQuests[currentIndex].rewardsString}")
                launchQuest(currentQuests[currentIndex])
            } else {
                val currentInvasions = CurrentInvasionData.currentInvasions
                if (currentInvasions.isEmpty()) {
                    Log.d(TAG, "No invasions available, attempting to fetch...")
                    fetchInvasions()
                    return@setOnClickListener
                }
                currentIndex = (currentIndex + 1) % currentInvasions.size
                Log.d(TAG, "Navigating to invasion at index $currentIndex: ${currentInvasions[currentIndex].lat}, ${currentInvasions[currentIndex].lng}")
                deletedInvasionsRepository.addDeletedInvasion(currentInvasions[currentIndex])
                showOverlayToast("Teleporting to ${currentInvasions[currentIndex].characterName} \n Daily Limit: ${deletedInvasionsRepository.getDeletionCountLast24Hours()}/900")
                launchMap(currentInvasions[currentIndex])
            }
        }

        leftBtn.setOnClickListener {
            if (mode == "quests") {
                val currentQuests = CurrentQuestData.currentQuests
                if (currentQuests.isEmpty()) {
                    Log.d(TAG, "No quests available, attempting to fetch...")
                    showOverlayToast("No quests available, fetching...")
                    fetchQuests()
                    return@setOnClickListener
                }
                currentIndex = if (currentIndex - 1 < 0) currentQuests.size - 1 else currentIndex - 1
                showOverlayToast("Teleporting to ${currentQuests[currentIndex].name}")
                launchQuest(currentQuests[currentIndex])
            } else {
                val currentInvasions = CurrentInvasionData.currentInvasions
                if (currentInvasions.isEmpty()) {
                    Log.d(TAG, "No invasions available, attempting to fetch...")
                    showOverlayToast("No invasions available, fetching...")
                    fetchInvasions()
                    return@setOnClickListener
                }
                currentIndex = if (currentIndex - 1 < 0) currentInvasions.size - 1 else currentIndex - 1
                showOverlayToast("Teleporting to ${currentInvasions[currentIndex].characterName}")
                launchMap(currentInvasions[currentIndex])
            }
        }
    }

    private fun updateOverlayBasedOnMode(mode: String) {
        if (mode == "quests") {
            showOverlayToast("Updated: Quests mode")
        } else {
            showOverlayToast("Updated: Invasions mode")
        }
    }

    private fun showOverlayMessage(message: String) {
        val statusText = overlayView?.findViewById<TextView>(R.id.status_text)
        if (statusText != null) {
            statusText.text = message
            statusText.visibility = View.VISIBLE
            Handler(Looper.getMainLooper()).postDelayed({
                statusText.visibility = View.GONE
            }, 3000)
        } else {
            Log.e(TAG, "Status text view not found, falling back to toast")
            Toast.makeText(this, message, Toast.LENGTH_SHORT).show()
        }
    }

    private fun fetchInvasions() {
        Log.d(TAG, "Fetching invasions...")
        cleanupObservers()
        viewModel = HomeViewModel(application)
        invasionsObserver = Observer { invasions ->
            Log.d(TAG, "Received ${invasions.size} invasions")
            CurrentInvasionData.currentInvasions = invasions.toMutableList()
            if (invasions.isNotEmpty()) {
                showOverlayToast("Found ${invasions.size} invasions")
                currentIndex = 0
            } else {
                showOverlayToast("No invasions found")
            }
            updateOverlayBasedOnMode("invasions")
        }
        errorObserver = Observer { errorMsg ->
            Log.e(TAG, "Error fetching invasions: $errorMsg")
            showOverlayToast("Error: $errorMsg")
        }
        viewModel?.invasions?.observeForever(invasionsObserver!!)
        viewModel?.error?.observeForever(errorObserver!!)
        viewModel?.fetchInvasions()
    }

    private fun fetchQuests() {
        Log.d(TAG, "Fetching quests...")

        val questsViewModel = QuestsViewModel(application)
        questsViewModel.fetchQuests()
    }

    private fun launchHome(lat: Double, lng: Double) {
        Log.d(TAG, "Launching home with coords: $lat, $lng")
        val url = "https://ipogo.app/?coords=$lat,$lng"
        Intent(Intent.ACTION_VIEW, url.toUri())
            .apply { addFlags(Intent.FLAG_ACTIVITY_NEW_TASK) }
            .also(::startActivity)
    }

    private fun launchMap(inv: Invasion) {
        Log.d(TAG, "Launching map with coords: ${inv.lat}, ${inv.lng}")
        val url = "https://ipogo.app/?coords=${inv.lat},${inv.lng}"
        Intent(Intent.ACTION_VIEW, url.toUri())
            .apply { addFlags(Intent.FLAG_ACTIVITY_NEW_TASK) }
            .also(::startActivity)
    }

    private fun launchQuest(quest: com.mints.projectgammatwo.data.Quests.Quest) {
        Log.d(TAG, "Launching quest map with coords: ${quest.lat}, ${quest.lng}")
        val url = "https://ipogo.app/?coords=${quest.lat},${quest.lng}"
        Intent(Intent.ACTION_VIEW, url.toUri())
            .apply { addFlags(Intent.FLAG_ACTIVITY_NEW_TASK) }
            .also(::startActivity)
    }

    private fun cleanupObservers() {
        invasionsObserver?.let { observer ->
            viewModel?.invasions?.removeObserver(observer)
        }
        errorObserver?.let { observer ->
            viewModel?.error?.removeObserver(observer)
        }
        invasionsObserver = null
        errorObserver = null
        viewModel = null
    }

    private fun showOverlayToast(message: String) {
        val toastOverlayView = LayoutInflater.from(this).inflate(R.layout.overlay_toast, null)
        val toastText = toastOverlayView.findViewById<TextView>(R.id.toast_text)
        toastText.text = message

        val params = WindowManager.LayoutParams().apply {
            width = WRAP_CONTENT
            height = WRAP_CONTENT
            type = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O)
                TYPE_APPLICATION_OVERLAY
            else
                TYPE_SYSTEM_ALERT
            flags = FLAG_NOT_FOCUSABLE or
                    FLAG_NOT_TOUCH_MODAL
            format = PixelFormat.TRANSLUCENT
            gravity = Gravity.BOTTOM or Gravity.CENTER_HORIZONTAL
            y = 100  // Distance from bottom
        }

        try {
            windowManager.addView(toastOverlayView, params)
            Handler(Looper.getMainLooper()).postDelayed({
                try {
                    windowManager.removeView(toastOverlayView)
                } catch (e: Exception) {
                    Log.e(TAG, "Error removing toast overlay: ${e.message}")
                }
            }, 1500)  // Show for 3 seconds
        } catch (e: Exception) {
            Log.e(TAG, "Error showing toast overlay: ${e.message}")
        }
    }

    override fun onDestroy() {
        super.onDestroy()
        Log.d(TAG, "Service onDestroy started")

        // Properly stop foreground service
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.N) {
            stopForeground(STOP_FOREGROUND_REMOVE)
        } else {
            stopForeground(true)
        }

        // Cancel notification explicitly
        val notificationManager = getSystemService(NOTIFICATION_SERVICE) as NotificationManager
        notificationManager.cancel(NOTIFICATION_ID)

        cleanupObservers()
        try {
            if (overlayView != null) {
                windowManager.removeView(overlayView)
                overlayView = null
            }
        } catch (e: Exception) {
            Log.e(TAG, "Error on destroy: ${e.message}")
        }

        // Clear overlay running state
        val sharedPrefs = getSharedPreferences("overlay_prefs", Context.MODE_PRIVATE)
        sharedPrefs.edit().putBoolean("overlay_running", false).apply()

        Log.d(TAG, "Service destroyed completely")
    }

    private fun setupFavoritesOverlay() {
        // Inflate the favorites overlay layout
        val contextThemeWrapper = ContextThemeWrapper(this, R.style.Theme_ProjectGamma2)

        favoritesOverlayView = LayoutInflater.from(contextThemeWrapper)
            .inflate(R.layout.favorites_overlay_layout, null)
        // Set up RecyclerView
        val recyclerView = favoritesOverlayView?.findViewById<RecyclerView>(R.id.favorites_recycler_view)
        recyclerView?.layoutManager = LinearLayoutManager(this)

        // Initialize adapter
        favoritesAdapter = OverlayFavoritesAdapter(
            onTeleportFavorite = { favorite ->
                // Teleport to location
                hideFilterOverlay()
                val url = "https://ipogo.app/?coords=${favorite.lat},${favorite.lng}"
                showOverlayToast("Teleporting to ${favorite.name}")
                Intent(Intent.ACTION_VIEW, url.toUri())
                    .apply { addFlags(Intent.FLAG_ACTIVITY_NEW_TASK) }
                    .also(::startActivity)
            }
        )

        recyclerView?.adapter = favoritesAdapter

        // Set up close button
        favoritesOverlayView?.findViewById<ImageButton>(R.id.close_favorites_button)?.setOnClickListener {
            hideFavoritesOverlay()
        }

        // Load favorites
        val favorites = FavoritesManager.getFavorites(this)
        favoritesAdapter.submitList(favorites)
    }

    private fun setupFiltersOverlay() {
        // Inflate the filter overlay layout
        val contextThemeWrapper = ContextThemeWrapper(this, R.style.Theme_ProjectGamma2)
        filterOverlayView = LayoutInflater.from(contextThemeWrapper)
            .inflate(R.layout.item_filters_overlay, null)

        // Set up RecyclerView
        val recyclerView = filterOverlayView?.findViewById<RecyclerView>(R.id.filters_recycler_view)
        recyclerView?.layoutManager = LinearLayoutManager(this)

        // Initialize the adapter with a filter selection callback
        filtersAdapter = FiltersRecyclerView { filterName ->
            Log.d(TAG, "Filter selected: $filterName")
            showOverlayToast("Applying filter: $filterName")
            applyFilter(filterName)
        }

        recyclerView?.adapter = filtersAdapter

        // Set up close button
        filterOverlayView?.findViewById<ImageButton>(R.id.close_filter_button)?.setOnClickListener {
            hideFiltersOverlay()
        }
    }

    private fun setupOverflowMenu() {
        val overflowButton = filterOverlayView?.findViewById<ImageButton>(R.id.overflow_menu_button)

        overflowButton?.setOnClickListener { view ->
            val popupMenu = PopupMenu(this, view)
            popupMenu.menuInflater.inflate(R.menu.menu_filters_sort, popupMenu.menu)

            // Check the currently active sort method
            when (currentSortOrder) {
                FilterSortOrder.DEFAULT -> popupMenu.menu.findItem(R.id.sort_default).isChecked = true
                FilterSortOrder.NAME -> popupMenu.menu.findItem(R.id.sort_by_name).isChecked = true
            }

            popupMenu.setOnMenuItemClickListener { item ->
                when (item.itemId) {
                    R.id.sort_default -> {
                        if (currentSortOrder != FilterSortOrder.DEFAULT) {
                            currentSortOrder = FilterSortOrder.DEFAULT
                            saveSortOrderPreference(FilterSortOrder.DEFAULT)
                            loadFiltersByMode() // Reload filters with new sort order
                        }
                        true
                    }
                    R.id.sort_by_name -> {
                        if (currentSortOrder != FilterSortOrder.NAME) {
                            currentSortOrder = FilterSortOrder.NAME
                            saveSortOrderPreference(FilterSortOrder.NAME)
                            loadFiltersByMode() // Reload filters with new sort order
                        }
                        true
                    }
                    else -> false
                }
            }

            popupMenu.show()
        }
    }

    private fun saveSortOrderPreference(sortOrder: FilterSortOrder) {
        val sharedPrefs = getSharedPreferences("app_preferences", MODE_PRIVATE)
        sharedPrefs.edit { putString(PREF_FILTER_SORT_ORDER, sortOrder.name) }
    }

    private fun loadSortOrderPreference() {
        val sharedPrefs = getSharedPreferences("app_preferences", MODE_PRIVATE)
        val savedSortOrder = sharedPrefs.getString(PREF_FILTER_SORT_ORDER, FilterSortOrder.DEFAULT.name)
        currentSortOrder = try {
            FilterSortOrder.valueOf(savedSortOrder ?: FilterSortOrder.DEFAULT.name)
        } catch (e: IllegalArgumentException) {
            FilterSortOrder.DEFAULT
        }
    }


    private fun showFiltersOverlay() {
        if (filterOverlayView == null) {
            setupFiltersOverlay()
        }

        // Load saved sort order preferences
        loadSortOrderPreference()

        // Setup the overflow menu
        setupOverflowMenu()

        // Hide main overlay first
        overlayView?.visibility = View.GONE

        // Add filters overlay to window manager if not already added
        if (filterOverlayView?.parent == null) {
            val params = WindowManager.LayoutParams().apply {
                width = WRAP_CONTENT
                height = WRAP_CONTENT
                type = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O)
                    TYPE_APPLICATION_OVERLAY
                else
                    TYPE_SYSTEM_ALERT
                flags = FLAG_NOT_FOCUSABLE
                format = PixelFormat.TRANSLUCENT
                gravity = Gravity.CENTER
            }

            val dragHandle = filterOverlayView?.findViewById<ImageButton>(R.id.drag_handle)
            dragHandle?.setOnTouchListener(DragTouchListener(params, windowManager, filterOverlayView!!))

            try {
                windowManager.addView(filterOverlayView, params)
                isFilterVisible = true
            } catch (e: Exception) {
                Log.e(TAG, "Error showing filters overlay: ${e.message}")
            }
        } else {
            // Just make it visible if already added
            filterOverlayView?.visibility = View.VISIBLE
            isFilterVisible = true
        }

        // Load the appropriate filter list based on current mode
        loadFiltersByMode()
    }

    private fun loadFiltersByMode() {
        val filterNames = if (currentMode == "quests") {
            // Load quest filter names
            filterPreferences.listQuestFilterNames()
        } else {
            // Load invasion/rocket filter names
            filterPreferences.listFilterNames()
        }

        // Apply sorting based on current sort order preference
        val sortedFilters = when (currentSortOrder) {
            FilterSortOrder.DEFAULT -> filterNames.toMutableList()  // Convert to MutableList
            FilterSortOrder.NAME -> filterNames.sortedBy { it.lowercase() }.toMutableList()  // Sort and convert to MutableList
        }

        // Update the adapter with the filter names
        filtersAdapter.submitList(sortedFilters)
    }

    private fun applyFilter(filterName: String) {
        val filterType = if (currentMode == "quests") "Quest" else "Rocket"

        // Apply the filter using the FilterPreferences
        filterPreferences.loadFilter(filterName, filterType)

        // Show a toast message
        showOverlayToast("Applied filter: $filterName")

        // Hide the filter overlay
        hideFiltersOverlay()

        // Refresh data based on selected filter
        if (currentMode == "quests") {
            fetchQuests()
        } else {
            fetchInvasions()
        }
    }

    private fun hideFiltersOverlay() {
        filterOverlayView?.visibility = View.GONE
        isFilterVisible = false

        // Show main overlay again
        overlayView?.visibility = View.VISIBLE
    }

    private fun showFavoritesOverlay() {
        if (favoritesOverlayView == null) {
            setupFavoritesOverlay()
        }

        // Load saved sort order preferences
        loadFavoritesSortOrderPreference()

        // Setup the overflow menu
        setupFavoritesOverflowMenu()

        // Hide main overlay first
        overlayView?.visibility = View.GONE

        // Add favorites overlay to window manager if not already added
        if (favoritesOverlayView?.parent == null) {
            val params = WindowManager.LayoutParams().apply {
                width = WRAP_CONTENT
                height = WRAP_CONTENT
                type = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O)
                    TYPE_APPLICATION_OVERLAY
                else
                    TYPE_SYSTEM_ALERT
                flags = FLAG_NOT_FOCUSABLE
                format = PixelFormat.TRANSLUCENT
                gravity = Gravity.CENTER
            }
            val dragHandle = favoritesOverlayView?.findViewById<ImageButton>(R.id.drag_handle)
            dragHandle?.setOnTouchListener(DragTouchListener(params, windowManager, favoritesOverlayView!!))

            try {
                windowManager.addView(favoritesOverlayView, params)
                isFavoritesVisible = true
            } catch (e: Exception) {
                Log.e(TAG, "Error showing favorites overlay: ${e.message}")
            }
        } else {
            // Just make it visible if already added
            favoritesOverlayView?.visibility = View.VISIBLE
            isFavoritesVisible = true
        }

        // Load favorites with the appropriate sort order
        loadFavoritesWithSort()
    }

    private fun setupFavoritesOverflowMenu() {
        val overflowButton = favoritesOverlayView?.findViewById<ImageButton>(R.id.overflow_menu_button)

        overflowButton?.setOnClickListener { view ->
            val popupMenu = PopupMenu(this, view)
            popupMenu.menuInflater.inflate(R.menu.overlay_favorites_menu, popupMenu.menu)

            // Check the currently active sort method
            when (currentFavoritesSortOrder) {
                FilterSortOrder.DEFAULT -> popupMenu.menu.findItem(R.id.sort_default).isChecked = true
                FilterSortOrder.NAME -> popupMenu.menu.findItem(R.id.sort_by_name).isChecked = true
            }

            popupMenu.setOnMenuItemClickListener { item ->
                when (item.itemId) {
                    R.id.sort_default -> {
                        if (currentFavoritesSortOrder != FilterSortOrder.DEFAULT) {
                            currentFavoritesSortOrder = FilterSortOrder.DEFAULT
                            saveFavoritesSortOrderPreference(FilterSortOrder.DEFAULT)
                            sortFavsByDefault()
                        }
                        true
                    }
                    R.id.sort_by_name -> {
                        if (currentFavoritesSortOrder != FilterSortOrder.NAME) {
                            currentFavoritesSortOrder = FilterSortOrder.NAME
                            saveFavoritesSortOrderPreference(FilterSortOrder.NAME)
                            sortFavsByName()
                        }
                        true
                    }
                    else -> false
                }
            }

            popupMenu.show()
        }
    }

    private fun saveFavoritesSortOrderPreference(sortOrder: FilterSortOrder) {
        val sharedPrefs = getSharedPreferences("app_preferences", MODE_PRIVATE)
        sharedPrefs.edit { putString(PREF_FAVORITES_SORT_ORDER, sortOrder.name) }
    }

    private fun loadFavoritesSortOrderPreference() {
        val sharedPrefs = getSharedPreferences("app_preferences", MODE_PRIVATE)
        val savedSortOrder = sharedPrefs.getString(PREF_FAVORITES_SORT_ORDER, FilterSortOrder.DEFAULT.name)
        currentFavoritesSortOrder = try {
            FilterSortOrder.valueOf(savedSortOrder ?: FilterSortOrder.DEFAULT.name)
        } catch (e: IllegalArgumentException) {
            FilterSortOrder.DEFAULT
        }
    }

    private fun sortFavsByName() {
        val favorites = FavoritesManager.getFavorites(this)
        val sortedList = favorites.sortedBy { it.name }
        favoritesAdapter.submitList(sortedList)
    }

    private fun sortFavsByDefault() {
        val favorites = FavoritesManager.getFavorites(this)
        favoritesAdapter.submitList(favorites)
    }

    private fun loadFavoritesWithSort() {
        val favorites = FavoritesManager.getFavorites(this)

        // Apply sorting based on current sort order preference
        val sortedFavorites = when (currentFavoritesSortOrder) {
            FilterSortOrder.DEFAULT -> favorites
            FilterSortOrder.NAME -> favorites.sortedBy { it.name }
        }

        favoritesAdapter.submitList(sortedFavorites)
    }

    private fun hideFavoritesOverlay() {
        favoritesOverlayView?.visibility = View.GONE
        isFavoritesVisible = false

        // Show main overlay again
        overlayView?.visibility = View.VISIBLE
    }

    private fun hideFilterOverlay() {
        filterOverlayView?.visibility = View.GONE
        isFilterVisible = false


        // Show main overlay again
        overlayView?.visibility = View.VISIBLE
    }

    private fun applyCustomizationToOverlay() {
        overlayView ?: return

        val buttonSize = customizationManager.getButtonSize()
        val buttonVisibility = customizationManager.getButtonVisibility()
        val buttonOrder = customizationManager.getButtonOrder()

        // Convert dp to pixels
        val buttonSizePx = (buttonSize * resources.displayMetrics.density).toInt()

        // Apply size and visibility to all buttons
        val buttonMap = mapOf(
            "drag_handle" to R.id.drag_handle,
            "close_button" to R.id.close_button,
            "right_button" to R.id.right_button,
            "left_button" to R.id.left_button,
            "home_button" to R.id.home_button,
            "refresh_button" to R.id.refresh_button,
            "switch_modes" to R.id.switch_modes,
            "filter_tab" to R.id.filter_tab,
            "favorites_tab" to R.id.favorites_tab
        )

        // Reorder buttons vertically based on saved order
        var topMargin = 0
        buttonOrder.forEach { buttonId ->
            val buttonViewId = buttonMap[buttonId]
            if (buttonViewId != null) {
                val button = overlayView?.findViewById<ImageButton>(buttonViewId)
                button?.let {
                    // Set size
                    val layoutParams = it.layoutParams as android.widget.FrameLayout.LayoutParams
                    layoutParams.width = buttonSizePx
                    layoutParams.height = buttonSizePx
                    layoutParams.topMargin = topMargin
                    it.layoutParams = layoutParams

                    // Set visibility
                    val isVisible = buttonVisibility[buttonId] ?: true
                    it.visibility = if (isVisible) View.VISIBLE else View.GONE

                    // Increment top margin for next button (only if visible)
                    if (isVisible) {
                        topMargin += buttonSizePx + (2 * resources.displayMetrics.density).toInt() // 2dp spacing
                    }
                }
            }
        }
    }

    private fun getButtonDisplayName(buttonId: String): String {
        return when (buttonId) {
            "drag_handle" -> "Drag Handle"
            "close_button" -> "Close"
            "right_button" -> "Next"
            "left_button" -> "Previous"
            "home_button" -> "Home"
            "refresh_button" -> "Refresh"
            "switch_modes" -> "Switch Mode"
            "filter_tab" -> "Filters"
            "favorites_tab" -> "Favorites"
            else -> buttonId
        }
    }

    private fun getButtonIcon(buttonId: String): Int {
        return when (buttonId) {
            "drag_handle" -> R.drawable.ic_drag_handle_overlay
            "close_button" -> R.drawable.close_24px
            "right_button" -> R.drawable.arrow_right_24px
            "left_button" -> R.drawable.arrow_left_24px
            "home_button" -> R.drawable.home_24px
            "refresh_button" -> R.drawable.refresh_24px
            "switch_modes" -> R.drawable.team_rocket_logo
            "filter_tab" -> R.drawable.tune_24px
            "favorites_tab" -> R.drawable.ic_favorite
            else -> R.drawable.ic_launcher_foreground
        }
    }


}

package com.mints.projectgammatwo.ui

import android.app.AlertDialog
import android.content.ClipData
import android.content.ComponentName
import android.content.Context
import android.content.Intent
import android.net.Uri
import android.os.Bundle
import android.text.InputType
import android.view.*
import android.widget.Button
import android.widget.EditText
import android.widget.Toast
import androidx.coordinatorlayout.widget.CoordinatorLayout
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.ItemTouchHelper
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.google.android.material.snackbar.Snackbar
import com.google.gson.Gson
import com.google.gson.reflect.TypeToken
import com.mints.projectgammatwo.R
import com.mints.projectgammatwo.data.FavoriteLocation
import com.mints.projectgammatwo.recyclerviews.FavoritesAdapter
import java.util.Collections
import androidx.core.content.edit

private const val PREFS_NAME = "FavoritesPrefs"
private const val SORT_ORDER_KEY = "sort_order"
private const val SORT_ORDER_NAME = "name"
private const val SORT_ORDER_DEFAULT = "default"


class FavoritesFragment : Fragment(), FavoriteDialogFragment.FavoriteDialogListener {

    private lateinit var recyclerView: RecyclerView
    private lateinit var adapter: FavoritesAdapter
    private lateinit var addFavoriteFab: View
    private var favoritesList = mutableListOf<FavoriteLocation>()
    private val gson = Gson()
    private val FAVORITES_PREFS_NAME = "favorites_prefs"
    private val KEY_FAVORITES = "favorites_list"
    private val FAVORITES_SORTED = "favorites_sorted"

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setHasOptionsMenu(true)
    }

    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {
        val view = inflater.inflate(R.layout.fragment_favorites, container, false)
        recyclerView = view.findViewById(R.id.favoritesRecyclerView)
        addFavoriteFab = view.findViewById(R.id.addFavoriteFab)
        return view
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        adapter = FavoritesAdapter(
            onDeleteFavorite = { favorite -> deleteFavorite(favorite) },
            onEditFavorite = { favorite, position -> showEditFavoriteDialog(favorite, position) },
            onCopyFavorite = { favorite -> copyFavorite(favorite) },
            onTeleportFavorite = { favorite -> teleportToFavorite(favorite) }
        )
        recyclerView.adapter = adapter
        recyclerView.layoutManager = LinearLayoutManager(requireContext())

        loadFavorites()



        addFavoriteFab.setOnClickListener {
            showAddFavoriteDialog()
        }
        val itemTouchHelper = ItemTouchHelper(itemTouchHelperCallback)
        itemTouchHelper.attachToRecyclerView(recyclerView)
    }


    override fun onCreateOptionsMenu(menu: Menu, inflater: MenuInflater) {
        inflater.inflate(R.menu.favorites_menu, menu)
        super.onCreateOptionsMenu(menu, inflater)
    }

    override fun onOptionsItemSelected(item: MenuItem): Boolean {
        return when (item.itemId) {
            R.id.menu_import_favorites -> {
                showImportFavoritesDialog()
                true
            }
            R.id.action_sortByName -> {
                saveSortOrderPreference(SORT_ORDER_NAME)
                sortFavsByName()
                true
            }

            R.id.action_sortByDefault -> {
                sortFavsByDefault()
                true
            }
            else -> super.onOptionsItemSelected(item)
        }
    }

    private fun saveSortOrderPreference(sortOrder: String) {
        val prefs = requireContext().getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
        prefs.edit { putString(SORT_ORDER_KEY, sortOrder) }
    }

    private fun sortFavsByName() {
        val sortedList = favoritesList.toMutableList().apply {
            sortBy { it.name }
        }
        adapter.submitList(sortedList)
    }

    private fun sortFavsByDefault() {
        loadFavorites()
    }



    private fun loadSortOrderPreference(): String {
        val prefs = requireContext().getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
        return prefs.getString(SORT_ORDER_KEY, SORT_ORDER_DEFAULT) ?: SORT_ORDER_DEFAULT
    }

    private fun applySavedSortOrder() {
        when (loadSortOrderPreference()) {
            SORT_ORDER_NAME -> sortFavsByName()
            SORT_ORDER_DEFAULT -> sortFavsByDefault()
        }
    }

    /**
     * Displays a dialog for importing favorites via JSON.
     */
    private fun showImportFavoritesDialog() {
        val builder = AlertDialog.Builder(requireContext())
        val inflater = requireActivity().layoutInflater
        val dialogView = inflater.inflate(R.layout.dialog_import_favorites, null)

        val editText = dialogView.findViewById<EditText>(R.id.editImportJson)
        val cancelButton = dialogView.findViewById<Button>(R.id.cancelImportButton)
        val importButton = dialogView.findViewById<Button>(R.id.importButton)

        builder.setView(dialogView)
        val dialog = builder.create()

        // Set up button click listeners
        cancelButton.setOnClickListener {
            dialog.dismiss()
        }

        importButton.setOnClickListener {
            val jsonString = editText.text.toString()
            if (jsonString.isBlank()) {
                Toast.makeText(requireContext(), "Input cannot be empty", Toast.LENGTH_SHORT).show()
            } else {
                importFavorites(jsonString)
                dialog.dismiss()
            }
        }

        dialog.show()
    }

    /**
     * Imports favorites from the provided JSON string.
     */
    private fun importFavorites(jsonString: String) {
        try {
            val importType = TypeToken
                .getParameterized(List::class.java, FavoriteLocation::class.java)
                .type
            val importedFavorites: List<FavoriteLocation> = gson.fromJson(jsonString, importType)
            // Merge imported favorites with the current list, avoiding duplicates.
            for (fav in importedFavorites) {
                if (!favoritesList.any { it.lat == fav.lat && it.lng == fav.lng && it.name == fav.name }) {
                    favoritesList.add(fav)
                }
            }
            adapter.submitList(favoritesList.toList())
            saveFavorites()
            Toast.makeText(requireContext(), "Favorites imported", Toast.LENGTH_SHORT).show()
        } catch (e: Exception) {
            e.printStackTrace()
            Toast.makeText(requireContext(), "Failed to import favorites", Toast.LENGTH_SHORT).show()
        }
    }


    private fun loadFavorites() {
        val prefs = requireContext().getSharedPreferences(FAVORITES_PREFS_NAME, Context.MODE_PRIVATE)

        val json = prefs.getString(KEY_FAVORITES, "[]") ?: "[]"
        val type = TypeToken
            .getParameterized(List::class.java, FavoriteLocation::class.java)
            .type
        val listType = TypeToken
            .getParameterized(List::class.java, FavoriteLocation::class.java)
            .type
        val loadedFavorites: List<FavoriteLocation> = gson.fromJson(json, listType)

        val orderJson = prefs.getString("favorites_order", "[]")
        val orderType = TypeToken
            .getParameterized(List::class.java, String::class.java)
            .type
        val originalOrder: List<String> = gson.fromJson(orderJson, orderType)

        // Reorder the loadedFavorites to match the original order
        favoritesList = if (originalOrder.isNotEmpty()) {
            loadedFavorites.sortedBy { originalOrder.indexOf(it.name) }.toMutableList()
        } else {
            loadedFavorites.toMutableList()
        }

        adapter.submitList(favoritesList.toList())
    }


    private fun saveFavorites() {
        val ctx = context ?: return
        val prefs = ctx
            .getSharedPreferences(FAVORITES_PREFS_NAME, Context.MODE_PRIVATE)
        val editor = prefs.edit()
        editor.putString(KEY_FAVORITES, gson.toJson(favoritesList))
        editor.putString("favorites_order", gson.toJson(favoritesList.map { it.name }))
        editor.apply()
    }



    private fun saveSortingOrder() {
        val prefs = requireContext().getSharedPreferences(FAVORITES_SORTED, Context.MODE_PRIVATE)
        prefs.edit().putString(KEY_FAVORITES, gson.toJson(favoritesList)).apply()

    }

    private fun deleteFavorite(favorite: FavoriteLocation) {
        val rootView = requireActivity().findViewById<View>(android.R.id.content)
        val deletedIndex = favoritesList.indexOf(favorite).takeIf { it != -1 } ?: return
        favoritesList.removeAt(deletedIndex)
        adapter.submitList(favoritesList.toList())
        saveFavorites()
        Snackbar.make(rootView, "Deleted: ${favorite.name}", Snackbar.LENGTH_LONG)
            .setAction("UNDO") {
                favoritesList.add(deletedIndex, favorite)
                adapter.submitList(favoritesList.toList())
                saveFavorites()
            }
            .show()
    }








    private fun copyFavorite(favorite: FavoriteLocation) {
        val clipboard = requireContext().getSystemService(Context.CLIPBOARD_SERVICE) as android.content.ClipboardManager
        val clip = ClipData.newPlainText("Favorite Coordinates", "${favorite.lat}, ${favorite.lng}")
        clipboard.setPrimaryClip(clip)
        Toast.makeText(requireContext(), "Coordinates copied to clipboard", Toast.LENGTH_SHORT).show()
    }


    /**
     * Teleports to the favorite location based on the user's teleport method preference.
     * If "ipogo" is selected, it opens an ipogo URL; if "joystick" is selected, it sends an intent.
     */
    private fun teleportToFavorite(favorite: FavoriteLocation) {
        val context = requireContext()
        val teleportPrefs = context.getSharedPreferences("teleport_prefs", Context.MODE_PRIVATE)
        val method = teleportPrefs.getString("teleport_method", "ipogo") ?: "ipogo"

        if (method == "ipogo") {
            val url = "https://ipogo.app/?coords=${favorite.lat},${favorite.lng}"
            val intent = Intent(Intent.ACTION_VIEW, Uri.parse(url))
            startActivity(intent)
            return
        }

        val baseIntent = Intent().apply {
            action = "theappninjas.gpsjoystick.TELEPORT"
            putExtra("lat", favorite.lat.toFloat())
            putExtra("lng", favorite.lng.toFloat())
        }
        val knownComponents = listOf(
            ComponentName(
                "com.theappninjas.fakegpsjoystick",
                "com.theappninjas.fakegpsjoystick.service.OverlayService"
            ),
            ComponentName(
                "com.thekkgqtaoxz.ymaaammipjyfatw",
                "com.thekkgqtaoxz.ymaaammipjyfatw.service.OverlayService"
            )
        )

        var serviceStarted = false
        for (component in knownComponents) {
            val intent = Intent(baseIntent).apply {
                this.component = component
            }
            try {
                val compName = context.startService(intent)
                if (compName != null) {
                    serviceStarted = true
                    break
                }
            } catch (e: Exception) {
            }
        }

        if (!serviceStarted) {
            val dynamicIntent = Intent(baseIntent).apply { component = null }
            val pm = context.packageManager
            val services = pm.queryIntentServices(dynamicIntent, 0)
            if (services.isNotEmpty()) {
                val serviceInfo = services.first().serviceInfo
                dynamicIntent.component = ComponentName(serviceInfo.packageName, serviceInfo.name)
                try {
                    val compName = context.startService(dynamicIntent)
                    serviceStarted = (compName != null)
                } catch (e: Exception) {
                }
            }
        }

        if (!serviceStarted) {
            Toast.makeText(context, "Error: Joystick not found", Toast.LENGTH_SHORT).show()
        }
    }







    /**
     * Opens the dialog to add a new favorite.
     */
    private fun showAddFavoriteDialog() {
        val dialog = FavoriteDialogFragment.newInstance(null, -1)
        dialog.setTargetFragment(this, 0)
        dialog.show(parentFragmentManager, "FavoriteDialogFragment")
    }

    /**
     * Opens the dialog to edit an existing favorite.
     */
    private fun showEditFavoriteDialog(favorite: FavoriteLocation, position: Int) {
        val dialog = FavoriteDialogFragment.newInstance(favorite, position)
        dialog.setTargetFragment(this, 0)
        dialog.show(parentFragmentManager, "FavoriteDialogFragment")
    }


    override fun onFavoriteSaved(favorite: FavoriteLocation, position: Int) {
        if (position == -1) {
            favoritesList.add(favorite)
            Toast.makeText(requireContext(), "Favorite added", Toast.LENGTH_SHORT).show()
        } else {
            favoritesList[position] = favorite
            Toast.makeText(requireContext(), "Favorite updated", Toast.LENGTH_SHORT).show()
        }
        adapter.submitList(favoritesList.toList())
        saveFavorites()
    }


    private val itemTouchHelperCallback = object : ItemTouchHelper.SimpleCallback(
        ItemTouchHelper.UP or ItemTouchHelper.DOWN, 0
    ) {
        override fun onMove(
            recyclerView: RecyclerView,
            viewHolder: RecyclerView.ViewHolder,
            target: RecyclerView.ViewHolder
        ): Boolean {
            // Use adapterPosition everywhere
            val from = viewHolder.adapterPosition
            val to   = target.adapterPosition
            if (from == RecyclerView.NO_POSITION || to == RecyclerView.NO_POSITION) return false

            // Make a mutable copy, swap, and resubmit
            // Update BOTH the favorites list AND the adapter
            Collections.swap(favoritesList, from, to)
            adapter.submitList(favoritesList.toList())
            return true
        }


        override fun onSwiped(viewHolder: RecyclerView.ViewHolder, direction: Int) {
            // No swipe action.
        }

        override fun clearView(recyclerView: RecyclerView, viewHolder: RecyclerView.ViewHolder) {
            super.clearView(recyclerView, viewHolder)
            saveFavorites()
        }
    }
}

package com.mints.projectgammatwo.ui

import android.app.AlertDialog
import android.content.ClipData
import android.content.ComponentName
import android.content.Context
import android.content.Intent
import android.net.Uri
import android.os.Bundle
import android.text.InputType
import android.view.*
import android.widget.Button
import android.widget.EditText
import android.widget.Toast
import androidx.coordinatorlayout.widget.CoordinatorLayout
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.ItemTouchHelper
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.google.android.material.snackbar.Snackbar
import com.google.gson.Gson
import com.google.gson.reflect.TypeToken
import com.mints.projectgammatwo.R
import com.mints.projectgammatwo.data.FavoriteLocation
import com.mints.projectgammatwo.recyclerviews.FavoritesAdapter
import java.util.Collections
import androidx.core.content.edit

private const val PREFS_NAME = "FavoritesPrefs"
private const val SORT_ORDER_KEY = "sort_order"
private const val SORT_ORDER_NAME = "name"
private const val SORT_ORDER_DEFAULT = "default"


class FavoritesFragment : Fragment(), FavoriteDialogFragment.FavoriteDialogListener {

    private lateinit var recyclerView: RecyclerView
    private lateinit var adapter: FavoritesAdapter
    private lateinit var addFavoriteFab: View
    private var favoritesList = mutableListOf<FavoriteLocation>()
    private val gson = Gson()
    private val FAVORITES_PREFS_NAME = "favorites_prefs"
    private val KEY_FAVORITES = "favorites_list"
    private val FAVORITES_SORTED = "favorites_sorted"

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setHasOptionsMenu(true)
    }

    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {
        val view = inflater.inflate(R.layout.fragment_favorites, container, false)
        recyclerView = view.findViewById(R.id.favoritesRecyclerView)
        addFavoriteFab = view.findViewById(R.id.addFavoriteFab)
        return view
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        adapter = FavoritesAdapter(
            onDeleteFavorite = { favorite -> deleteFavorite(favorite) },
            onEditFavorite = { favorite, position -> showEditFavoriteDialog(favorite, position) },
            onCopyFavorite = { favorite -> copyFavorite(favorite) },
            onTeleportFavorite = { favorite -> teleportToFavorite(favorite) }
        )
        recyclerView.adapter = adapter
        recyclerView.layoutManager = LinearLayoutManager(requireContext())

        loadFavorites()



        addFavoriteFab.setOnClickListener {
            showAddFavoriteDialog()
        }
        val itemTouchHelper = ItemTouchHelper(itemTouchHelperCallback)
        itemTouchHelper.attachToRecyclerView(recyclerView)
    }


    override fun onCreateOptionsMenu(menu: Menu, inflater: MenuInflater) {
        inflater.inflate(R.menu.favorites_menu, menu)
        super.onCreateOptionsMenu(menu, inflater)
    }

    override fun onOptionsItemSelected(item: MenuItem): Boolean {
        return when (item.itemId) {
            R.id.menu_import_favorites -> {
                showImportFavoritesDialog()
                true
            }
            R.id.menu_import_hotspots -> {
                showImportHotspotsDialog()
                true
            }
            R.id.action_sortByName -> {
                saveSortOrderPreference(SORT_ORDER_NAME)
                sortFavsByName()
                true
            }

            R.id.action_sortByDefault -> {
                sortFavsByDefault()
                true
            }
            else -> super.onOptionsItemSelected(item)
        }
    }

    private fun saveSortOrderPreference(sortOrder: String) {
        val prefs = requireContext().getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
        prefs.edit { putString(SORT_ORDER_KEY, sortOrder) }
    }

    private fun sortFavsByName() {
        val sortedList = favoritesList.toMutableList().apply {
            sortBy { it.name }
        }
        adapter.submitList(sortedList)
    }

    private fun sortFavsByDefault() {
        loadFavorites()
    }



    private fun loadSortOrderPreference(): String {
        val prefs = requireContext().getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
        return prefs.getString(SORT_ORDER_KEY, SORT_ORDER_DEFAULT) ?: SORT_ORDER_DEFAULT
    }

    private fun applySavedSortOrder() {
        when (loadSortOrderPreference()) {
            SORT_ORDER_NAME -> sortFavsByName()
            SORT_ORDER_DEFAULT -> sortFavsByDefault()
        }
    }

    /**
     * Displays a dialog for importing favorites via JSON.
     */
    private fun showImportFavoritesDialog() {
        val builder = AlertDialog.Builder(requireContext())
        val inflater = requireActivity().layoutInflater
        val dialogView = inflater.inflate(R.layout.dialog_import_favorites, null)

        val editText = dialogView.findViewById<EditText>(R.id.editImportJson)
        val cancelButton = dialogView.findViewById<Button>(R.id.cancelImportButton)
        val importButton = dialogView.findViewById<Button>(R.id.importButton)

        builder.setView(dialogView)
        val dialog = builder.create()

        // Set up button click listeners
        cancelButton.setOnClickListener {
            dialog.dismiss()
        }

        importButton.setOnClickListener {
            val jsonString = editText.text.toString()
            if (jsonString.isBlank()) {
                Toast.makeText(requireContext(), "Input cannot be empty", Toast.LENGTH_SHORT).show()
            } else {
                importFavorites(jsonString)
                dialog.dismiss()
            }
        }

        dialog.show()
    }

    /**
     * Displays a confirmation dialog for importing hotspots.
     */
    private fun showImportHotspotsDialog() {
        val builder = AlertDialog.Builder(requireContext())
        val inflater = requireActivity().layoutInflater
        val dialogView = inflater.inflate(R.layout.dialog_confirm_import_hotspots, null)

        val cancelButton = dialogView.findViewById<Button>(R.id.cancelConfirmButton)
        val confirmButton = dialogView.findViewById<Button>(R.id.confirmImportButton)

        builder.setView(dialogView)
        val dialog = builder.create()

        // Set up button click listeners
        cancelButton.setOnClickListener {
            dialog.dismiss()
        }

        confirmButton.setOnClickListener {
            importHotspots()
            dialog.dismiss()
        }

        dialog.show()
    }

    /**
     * Imports hotspots from the hotspots.txt file in the res folder.
     */
    private fun importHotspots() {
        try {
            val inputStream = resources.openRawResource(R.raw.hotspots)
            val jsonString = inputStream.bufferedReader().use { it.readText() }

            val importType = TypeToken
                .getParameterized(List::class.java, FavoriteLocation::class.java)
                .type
            val importedHotspots: List<FavoriteLocation> = gson.fromJson(jsonString, importType)

            // Store the current list before making changes
            val previousList = favoritesList.toList()
            val addedLocations = mutableListOf<FavoriteLocation>()

            // Merge imported hotspots with the current list, avoiding duplicates
            for (hotspot in importedHotspots) {
                if (!favoritesList.any { it.lat == hotspot.lat && it.lng == hotspot.lng && it.name == hotspot.name }) {
                    favoritesList.add(hotspot)
                    addedLocations.add(hotspot)
                }
            }

            adapter.submitList(favoritesList.toList())
            saveFavorites()

            // Show snackbar with undo option
            val rootView = requireActivity().findViewById<View>(android.R.id.content)
            val message = if (addedLocations.isNotEmpty()) {
                "Imported ${addedLocations.size} hotspot(s)"
            } else {
                "No new hotspots to import"
            }

            Snackbar.make(rootView, message, Snackbar.LENGTH_LONG)
                .setAction("UNDO") {
                    // Restore the previous list
                    favoritesList.clear()
                    favoritesList.addAll(previousList)
                    adapter.submitList(favoritesList.toList())
                    saveFavorites()
                    Toast.makeText(requireContext(), "Import undone", Toast.LENGTH_SHORT).show()
                }
                .show()
        } catch (e: Exception) {
            e.printStackTrace()
            Toast.makeText(requireContext(), "Failed to import hotspots: ${e.message}", Toast.LENGTH_SHORT).show()
        }
    }


    /**
     * Imports favorites from the provided JSON string.
     */
    private fun importFavorites(jsonString: String) {
        try {
            val importType = TypeToken
                .getParameterized(List::class.java, FavoriteLocation::class.java)
                .type
            val importedFavorites: List<FavoriteLocation> = gson.fromJson(jsonString, importType)
            // Merge imported favorites with the current list, avoiding duplicates.
            for (fav in importedFavorites) {
                if (!favoritesList.any { it.lat == fav.lat && it.lng == fav.lng && it.name == fav.name }) {
                    favoritesList.add(fav)
                }
            }
            adapter.submitList(favoritesList.toList())
            saveFavorites()
            Toast.makeText(requireContext(), "Favorites imported", Toast.LENGTH_SHORT).show()
        } catch (e: Exception) {
            e.printStackTrace()
            Toast.makeText(requireContext(), "Failed to import favorites", Toast.LENGTH_SHORT).show()
        }
    }


    private fun loadFavorites() {
        val prefs = requireContext().getSharedPreferences(FAVORITES_PREFS_NAME, Context.MODE_PRIVATE)

        val json = prefs.getString(KEY_FAVORITES, "[]") ?: "[]"
        val type = TypeToken
            .getParameterized(List::class.java, FavoriteLocation::class.java)
            .type
        val listType = TypeToken
            .getParameterized(List::class.java, FavoriteLocation::class.java)
            .type
        val loadedFavorites: List<FavoriteLocation> = gson.fromJson(json, listType)

        val orderJson = prefs.getString("favorites_order", "[]")
        val orderType = TypeToken
            .getParameterized(List::class.java, String::class.java)
            .type
        val originalOrder: List<String> = gson.fromJson(orderJson, orderType)

        // Reorder the loadedFavorites to match the original order
        favoritesList = if (originalOrder.isNotEmpty()) {
            loadedFavorites.sortedBy { originalOrder.indexOf(it.name) }.toMutableList()
        } else {
            loadedFavorites.toMutableList()
        }

        adapter.submitList(favoritesList.toList())
    }


    private fun saveFavorites() {
        val ctx = context ?: return
        val prefs = ctx
            .getSharedPreferences(FAVORITES_PREFS_NAME, Context.MODE_PRIVATE)
        val editor = prefs.edit()
        editor.putString(KEY_FAVORITES, gson.toJson(favoritesList))
        editor.putString("favorites_order", gson.toJson(favoritesList.map { it.name }))
        editor.apply()
    }



    private fun saveSortingOrder() {
        val prefs = requireContext().getSharedPreferences(FAVORITES_SORTED, Context.MODE_PRIVATE)
        prefs.edit().putString(KEY_FAVORITES, gson.toJson(favoritesList)).apply()

    }

    private fun deleteFavorite(favorite: FavoriteLocation) {
        val rootView = requireActivity().findViewById<View>(android.R.id.content)
        val deletedIndex = favoritesList.indexOf(favorite).takeIf { it != -1 } ?: return
        favoritesList.removeAt(deletedIndex)
        adapter.submitList(favoritesList.toList())
        saveFavorites()
        Snackbar.make(rootView, "Deleted: ${favorite.name}", Snackbar.LENGTH_LONG)
            .setAction("UNDO") {
                favoritesList.add(deletedIndex, favorite)
                adapter.submitList(favoritesList.toList())
                saveFavorites()
            }
            .show()
    }








    private fun copyFavorite(favorite: FavoriteLocation) {
        val clipboard = requireContext().getSystemService(Context.CLIPBOARD_SERVICE) as android.content.ClipboardManager
        val clip = ClipData.newPlainText("Favorite Coordinates", "${favorite.lat}, ${favorite.lng}")
        clipboard.setPrimaryClip(clip)
        Toast.makeText(requireContext(), "Coordinates copied to clipboard", Toast.LENGTH_SHORT).show()
    }


    /**
     * Teleports to the favorite location based on the user's teleport method preference.
     * If "ipogo" is selected, it opens an ipogo URL; if "joystick" is selected, it sends an intent.
     */
    private fun teleportToFavorite(favorite: FavoriteLocation) {
        val context = requireContext()
        val teleportPrefs = context.getSharedPreferences("teleport_prefs", Context.MODE_PRIVATE)
        val method = teleportPrefs.getString("teleport_method", "ipogo") ?: "ipogo"

        if (method == "ipogo") {
            val url = "https://ipogo.app/?coords=${favorite.lat},${favorite.lng}"
            val intent = Intent(Intent.ACTION_VIEW, Uri.parse(url))
            startActivity(intent)
            return
        }

        val baseIntent = Intent().apply {
            action = "theappninjas.gpsjoystick.TELEPORT"
            putExtra("lat", favorite.lat.toFloat())
            putExtra("lng", favorite.lng.toFloat())
        }
        val knownComponents = listOf(
            ComponentName(
                "com.theappninjas.fakegpsjoystick",
                "com.theappninjas.fakegpsjoystick.service.OverlayService"
            ),
            ComponentName(
                "com.thekkgqtaoxz.ymaaammipjyfatw",
                "com.thekkgqtaoxz.ymaaammipjyfatw.service.OverlayService"
            )
        )

        var serviceStarted = false
        for (component in knownComponents) {
            val intent = Intent(baseIntent).apply {
                this.component = component
            }
            try {
                val compName = context.startService(intent)
                if (compName != null) {
                    serviceStarted = true
                    break
                }
            } catch (e: Exception) {
            }
        }

        if (!serviceStarted) {
            val dynamicIntent = Intent(baseIntent).apply { component = null }
            val pm = context.packageManager
            val services = pm.queryIntentServices(dynamicIntent, 0)
            if (services.isNotEmpty()) {
                val serviceInfo = services.first().serviceInfo
                dynamicIntent.component = ComponentName(serviceInfo.packageName, serviceInfo.name)
                try {
                    val compName = context.startService(dynamicIntent)
                    serviceStarted = (compName != null)
                } catch (e: Exception) {
                }
            }
        }

        if (!serviceStarted) {
            Toast.makeText(context, "Error: Joystick not found", Toast.LENGTH_SHORT).show()
        }
    }







    /**
     * Opens the dialog to add a new favorite.
     */
    private fun showAddFavoriteDialog() {
        val dialog = FavoriteDialogFragment.newInstance(null, -1)
        dialog.setTargetFragment(this, 0)
        dialog.show(parentFragmentManager, "FavoriteDialogFragment")
    }

    /**
     * Opens the dialog to edit an existing favorite.
     */
    private fun showEditFavoriteDialog(favorite: FavoriteLocation, position: Int) {
        val dialog = FavoriteDialogFragment.newInstance(favorite, position)
        dialog.setTargetFragment(this, 0)
        dialog.show(parentFragmentManager, "FavoriteDialogFragment")
    }


    override fun onFavoriteSaved(favorite: FavoriteLocation, position: Int) {
        if (position == -1) {
            favoritesList.add(favorite)
            Toast.makeText(requireContext(), "Favorite added", Toast.LENGTH_SHORT).show()
        } else {
            favoritesList[position] = favorite
            Toast.makeText(requireContext(), "Favorite updated", Toast.LENGTH_SHORT).show()
        }
        adapter.submitList(favoritesList.toList())
        saveFavorites()
    }


    private val itemTouchHelperCallback = object : ItemTouchHelper.SimpleCallback(
        ItemTouchHelper.UP or ItemTouchHelper.DOWN, 0
    ) {
        override fun onMove(
            recyclerView: RecyclerView,
            viewHolder: RecyclerView.ViewHolder,
            target: RecyclerView.ViewHolder
        ): Boolean {
            // Use adapterPosition everywhere
            val from = viewHolder.adapterPosition
            val to   = target.adapterPosition
            if (from == RecyclerView.NO_POSITION || to == RecyclerView.NO_POSITION) return false

            // Make a mutable copy, swap, and resubmit
            // Update BOTH the favorites list AND the adapter
            Collections.swap(favoritesList, from, to)
            adapter.submitList(favoritesList.toList())
            return true
        }


        override fun onSwiped(viewHolder: RecyclerView.ViewHolder, direction: Int) {
            // No swipe action.
        }

        override fun clearView(recyclerView: RecyclerView, viewHolder: RecyclerView.ViewHolder) {
            super.clearView(recyclerView, viewHolder)
            saveFavorites()
        }
    }
}

package com.mints.projectgammatwo.ui

import android.app.AlertDialog
import android.content.Context
import android.content.SharedPreferences
import android.content.res.ColorStateList
import android.graphics.Color
import android.os.Bundle
import android.text.InputType
import android.util.Log
import android.util.TypedValue
import android.view.Gravity
import android.view.LayoutInflater
import android.view.Menu
import android.view.MenuInflater
import android.view.MenuItem
import android.view.View
import android.view.ViewGroup
import android.widget.Button
import android.widget.CheckBox
import android.widget.CompoundButton
import android.widget.EditText
import android.widget.ImageButton
import android.widget.LinearLayout
import android.widget.RadioGroup
import android.widget.TextView
import android.widget.Toast
import androidx.core.content.ContextCompat
import androidx.core.view.setPadding
import androidx.fragment.app.Fragment
import androidx.lifecycle.ViewModelProvider
import com.google.android.material.button.MaterialButton
import com.google.gson.Gson
import com.mints.projectgammatwo.R
import com.mints.projectgammatwo.data.CurrentQuestData
import com.mints.projectgammatwo.data.DataMappings
import com.mints.projectgammatwo.data.FilterPreferences
// Removed PokemonRepository as it's unused in this fragment
import com.mints.projectgammatwo.data.Quests
import com.mints.projectgammatwo.viewmodels.QuestsViewModel

class FilterFragment : Fragment() {

    private lateinit var filterPreferences: FilterPreferences
    private val enabledRocketFilters = mutableSetOf<Int>()
    private lateinit var questsViewModel: QuestsViewModel
    private var currentFilterType = "Rocket"
    private lateinit var questPrefs: SharedPreferences
    private val enabledQuestFilters = mutableSetOf<String>()
    private lateinit var questLayout: LinearLayout
    private lateinit var rocketLayoutGlobal: LinearLayout
    private lateinit var currentFilterTextView: TextView
    private var originalSettingsOfLoadedRocketFilter: Set<Int>? = null
    private var originalSettingsOfLoadedQuestFilter: Set<String>? = null


    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setHasOptionsMenu(true)
    }

    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        return inflater.inflate(R.layout.fragment_list, container, false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        filterPreferences = FilterPreferences(requireContext())
        questPrefs = requireContext().getSharedPreferences("quest_filters", Context.MODE_PRIVATE)
        currentFilterTextView = view.findViewById(R.id.currentFilterText)
        rocketLayoutGlobal = view.findViewById(R.id.rocketFiltersLayout)
        questLayout = view.findViewById(R.id.questFiltersLayout)
        enabledRocketFilters.clear()
        enabledRocketFilters.addAll(filterPreferences.getEnabledCharacters())

        enabledQuestFilters.clear()
        enabledQuestFilters.addAll(filterPreferences.getEnabledQuestFilters())

        questsViewModel = ViewModelProvider(this)[QuestsViewModel::class.java]
        questsViewModel.fetchSpindaFormsFromApi()

        questsViewModel.spindaFormsLiveData.observe(viewLifecycleOwner) { spindaFormsMap ->
            Log.d("FilterFragment", "spindaFormsLiveData emitted: ${spindaFormsMap.keys}")
            DataMappings.initializePokemonData(requireContext()) {
                if (!isAdded) return@initializePokemonData
                Log.d("App", "Pokemon data loaded with ${DataMappings.pokemonEncounterMapNew.size} entries")

                setupQuestFilters(questLayout)
            }

        }

        val testList = CurrentQuestData.currentQuests
        Log.d("FilterFragment", "Current quests size: ${testList.size}")

        val spindaQuests = testList.filter { quest ->
            quest.rewardsIds.split(",").any { it == "327" }
        }
        val spindaType1 = spindaQuests.filter { quest ->
            quest.rewardsString.contains("01")

        }
        val spindaType2 = spindaQuests.filter { quest ->
            quest.rewardsString.contains("02")
        }

        Log.d("FilterFragment", "Spinda (01) quests: ${spindaType1.size}")
        Log.d("FilterFragment", "Spinda (02) quests: ${spindaType2.size}")


        getAvailableSpindaForms()
        questsViewModel.questsLiveData.observe(viewLifecycleOwner) { quests ->
            getAvailableSpindaForms()
        }

        val radioGroup = view.findViewById<RadioGroup>(R.id.filterTypeRadioGroup)

        radioGroup.post {
            val isQuestVisible = questLayout.visibility == View.VISIBLE
            val isRocketVisible = rocketLayoutGlobal.visibility == View.VISIBLE

            when {
                isQuestVisible -> {
                    radioGroup.check(R.id.rbQuest)
                    updateCurrentQuestFilter()
                    currentFilterType = "Quest"
                    Log.d("Test","Current filter type: $currentFilterType")
                }
                isRocketVisible -> {
                    radioGroup.check(R.id.rbRocket)
                    updateCurrentRocketFilter()
                }
                else -> {
                    radioGroup.check(R.id.rbRocket)
                    rocketLayoutGlobal.visibility = View.VISIBLE
                    questLayout.visibility = View.GONE
                    updateCurrentRocketFilter()
                    currentFilterType = "Rocket"
                    Log.d("Test","Current filter type: $currentFilterType")

                }
            }
            activity?.invalidateOptionsMenu()


            radioGroup.setOnCheckedChangeListener { _, checkedId ->
                when (checkedId) {
                    R.id.rbRocket -> {
                        rocketLayoutGlobal.visibility = View.VISIBLE
                        questLayout.visibility = View.GONE
                        currentFilterType = "Rocket"
                        updateCurrentRocketFilter()
                    }
                    R.id.rbQuest -> {
                        rocketLayoutGlobal.visibility = View.GONE
                        questLayout.visibility = View.VISIBLE
                        currentFilterType = "Quest"

                        updateCurrentQuestFilter()
                    }
                }
                activity?.invalidateOptionsMenu()
            }
        }







        val initialActiveRocketFilter = filterPreferences.getActiveRocketFilter()
        if (initialActiveRocketFilter.isNotEmpty()) {
            if (originalSettingsOfLoadedRocketFilter == null) {
                originalSettingsOfLoadedRocketFilter = HashSet(filterPreferences.getEnabledCharacters())
            }
        }

        val initialActiveQuestFilter = filterPreferences.getActiveQuestFilter()
        if (initialActiveQuestFilter.isNotEmpty()) {
            if (originalSettingsOfLoadedQuestFilter == null) {
                originalSettingsOfLoadedQuestFilter = HashSet(filterPreferences.getEnabledQuestFilters())
            }
        }

        setupRocketFilters(rocketLayoutGlobal)
    }

    override fun onCreateOptionsMenu(menu: Menu, inflater: MenuInflater) {
        inflater.inflate(R.menu.filter_nav_menu, menu)


        super.onCreateOptionsMenu(menu, inflater)
    }

    override fun onPrepareOptionsMenu(menu: Menu) {
        super.onPrepareOptionsMenu(menu)
        val saveRocketItem = menu.findItem(R.id.action_save_rocket)
        val saveQuestItem  = menu.findItem(R.id.action_save_quest)

        saveRocketItem?.isVisible = (currentFilterType == "Rocket")
        saveQuestItem?.isVisible  = (currentFilterType == "Quest")
    }


    override fun onOptionsItemSelected(item: MenuItem): Boolean {
        return when (item.itemId) {
            R.id.action_save_rocket -> {
                showSaveFilterDialog(true)
                true
            }
            R.id.action_save_quest -> {
                showSaveFilterDialog(false)
                true
            }
            R.id.action_refresh_filters -> {
                questsViewModel.fetchQuests()
                if (::questLayout.isInitialized) {
                    setupQuestFilters(questLayout)
                }
                if (::rocketLayoutGlobal.isInitialized) {
                    setupRocketFilters(rocketLayoutGlobal)
                }
                true
            }
            else -> super.onOptionsItemSelected(item)
        }
    }

    private fun showSaveFilterDialog(isRocket: Boolean) {
        val builder = AlertDialog.Builder(requireContext())
        val inflater = requireActivity().layoutInflater
        val dialogView = inflater.inflate(R.layout.dialog_save_filter, null)

        val titleTextView = dialogView.findViewById<TextView>(R.id.saveFilterTitle)
        val editText = dialogView.findViewById<EditText>(R.id.editFilterName)
        val cancelButton = dialogView.findViewById<Button>(R.id.cancelFilterButton)
        val saveButton = dialogView.findViewById<Button>(R.id.saveFilterButton)

        // Set the dynamic title
        val type = if(isRocket) "rocket" else "quest"
        titleTextView.text = "Enter a name for the new $type filter"

        builder.setView(dialogView)
        val dialog = builder.create()

        // Set up button click listeners
        cancelButton.setOnClickListener {
            dialog.dismiss()
        }

        saveButton.setOnClickListener {
            val newFilterName = editText.text.toString().trim()
            if (newFilterName.isEmpty()) {
                Toast.makeText(requireContext(), "Please enter a name", Toast.LENGTH_SHORT).show()
                return@setOnClickListener
            }

            if (isRocket) {
                filterPreferences.saveEnabledCharacters(enabledRocketFilters)
                filterPreferences.saveCurrentAsFilter(newFilterName)
                Toast.makeText(requireContext(), "Filter '$newFilterName' saved", Toast.LENGTH_SHORT).show()
                val activeFilterNameToRevert = filterPreferences.getActiveRocketFilter()

                if (activeFilterNameToRevert.isNotEmpty() && originalSettingsOfLoadedRocketFilter != null) {
                    enabledRocketFilters.clear()
                    enabledRocketFilters.addAll(originalSettingsOfLoadedRocketFilter!!)
                    filterPreferences.saveEnabledCharacters(enabledRocketFilters)
                } else {
                    filterPreferences.setActiveRocketFilter(newFilterName)
                    originalSettingsOfLoadedRocketFilter = HashSet(enabledRocketFilters)
                }
                setupRocketFilters(rocketLayoutGlobal)

            } else {
                filterPreferences.saveEnabledQuestFilters(enabledQuestFilters)
                filterPreferences.saveCurrentQuestFilter(newFilterName)
                Log.d("SaveFilter","Current enabled spinda forms: ${filterPreferences.getEnabledSpindaForms()}")

                Toast.makeText(requireContext(), "Filter '$newFilterName' saved", Toast.LENGTH_SHORT).show()

                val activeFilterNameToRevert = filterPreferences.getActiveQuestFilter()
                if (activeFilterNameToRevert.isNotEmpty() && originalSettingsOfLoadedQuestFilter != null) {
                    enabledQuestFilters.clear()
                    enabledQuestFilters.addAll(originalSettingsOfLoadedQuestFilter!!)
                    filterPreferences.saveEnabledQuestFilters(enabledQuestFilters)
                } else {
                    filterPreferences.setActiveQuestFilter(newFilterName)
                    originalSettingsOfLoadedQuestFilter = HashSet(enabledQuestFilters)
                }
                setupQuestFilters(questLayout)
            }
            dialog.dismiss()
        }

        dialog.show()
    }


    private fun setupRocketFilters(parent: LinearLayout) {
        parent.removeAllViews()
        addResetButton(parent, "Rocket")
        addToggleAllButton(parent, "Rocket")
        addSectionHeader(parent, "Rocket Filters")
        addSelectFilterButton(parent, "Rocket")

        updateCurrentRocketFilter()

        DataMappings.characterNamesMap.forEach { (id, name) ->
            addCheckBox(parent, name, id, enabledRocketFilters) { checked ->
                if (checked) enabledRocketFilters.add(id) else enabledRocketFilters.remove(id)

                filterPreferences.saveEnabledCharacters(enabledRocketFilters)
            }
        }
    }

    private fun updateCurrentRocketFilter() {

        if(currentFilterType != "Rocket") {
            updateCurrentQuestFilter()
        }
        val currentFilterName = filterPreferences.getActiveRocketFilter()
        if (currentFilterName.isNotEmpty()) {
            currentFilterTextView.visibility = View.VISIBLE
            currentFilterTextView.text = "Current selected filter: $currentFilterName"
        } else {
            currentFilterTextView.visibility = View.VISIBLE
            currentFilterTextView.text = "Current selected filter: Unsaved"
        }
    }
    private fun updateCurrentQuestFilter() {

        if(currentFilterType != "Quest") {
            updateCurrentRocketFilter()
        }


        val currentFilterName = filterPreferences.getActiveQuestFilter()
        if (currentFilterName.isNotEmpty()) {
            currentFilterTextView.visibility = View.VISIBLE
            currentFilterTextView.text = "Current selected filter: $currentFilterName"
        } else {
            currentFilterTextView.visibility = View.VISIBLE
            currentFilterTextView.text = "Current selected filter: Unsaved"
        }
    }


    private fun addResetButton(parent: LinearLayout, filterType: String) {
        val resetButton = MaterialButton(requireContext()).apply {
            text = "Reset $filterType Filters"
            setPadding(16, 8, 16, 8)
            setOnClickListener {
                when (filterType) {
                    "Rocket" -> {
                        enabledRocketFilters.clear()
                        filterPreferences.saveEnabledCharacters(enabledRocketFilters)
                        setupRocketFilters(parent)
                    }
                    "Quest" -> {
                        enabledQuestFilters.clear()
                        filterPreferences.saveEnabledQuestFilters(enabledQuestFilters)
                        setupQuestFilters(parent)
                    }
                }
            }
        }
        parent.addView(resetButton, 0)
    }

    private fun addSelectFilterButton(parent: LinearLayout, filterType: String) {
        val selectButton = MaterialButton(requireContext()).apply {
            text = "Select $filterType Filter"
            setPadding(16, 8, 16, 8)
            setOnClickListener {
                showSelectFilterDialog(parent, filterType)
            }
        }
        parent.addView(selectButton, 1)
    }

    private fun showSelectFilterDialog(parentLayoutForRefresh: LinearLayout, filterType: String) {
        val builder = AlertDialog.Builder(requireContext())
        val inflater = requireActivity().layoutInflater
        val dialogView = inflater.inflate(R.layout.dialog_select_filter, null)

        val title = dialogView.findViewById<TextView>(R.id.selectFilterTitle)
        val listContainer = dialogView.findViewById<LinearLayout>(R.id.filterListContainer)
        val cancelButton = dialogView.findViewById<Button>(R.id.cancelSelectButton)

        title.text = "Select $filterType Filter"

        builder.setView(dialogView)
        val dialog = builder.create()

        // Set up cancel button
        cancelButton.setOnClickListener {
            dialog.dismiss()
        }

        fun updateDialogContent() {
            listContainer.removeAllViews()

            val filterNames: Array<String> = if (filterType == "Rocket") {
                filterPreferences.listFilterNames().toTypedArray()
            } else {
                filterPreferences.listQuestFilterNames().toTypedArray()
            }

            if (filterNames.isEmpty()) {
                val emptyView = TextView(requireContext()).apply {
                    text = "No saved filters available"
                    setPadding(16, 16, 16, 16)
                    gravity = android.view.Gravity.CENTER
                    textSize = 16f
                    setTextColor(ContextCompat.getColor(requireContext(), android.R.color.darker_gray))
                }
                listContainer.addView(emptyView)
                return
            }

            filterNames.forEach { filterName ->
                val itemView = inflater.inflate(R.layout.filter_list_item, listContainer, false)
                val nameTextView = itemView.findViewById<TextView>(R.id.filterNameText)
                val deleteButton = itemView.findViewById<ImageButton>(R.id.deleteFilterButton)
                val selectButton = itemView.findViewById<Button>(R.id.selectFilterButton)

                nameTextView.text = filterName

                selectButton.setOnClickListener {
                    if (filterType == "Rocket") {
                        filterPreferences.loadFilter(filterName, "Rocket")
                        enabledRocketFilters.clear()
                        enabledRocketFilters.addAll(filterPreferences.getEnabledCharacters())
                        filterPreferences.getEnabledSpindaForms()
                        Log.d("SelectingFilter","Spinda forms enabled: ${filterPreferences.getEnabledSpindaForms()}")

                        originalSettingsOfLoadedRocketFilter = HashSet(enabledRocketFilters)
                        setupRocketFilters(parentLayoutForRefresh)
                    } else {
                        filterPreferences.loadFilter(filterName, "Quest")
                        enabledQuestFilters.clear()
                        enabledQuestFilters.addAll(filterPreferences.getEnabledQuestFilters())

                        originalSettingsOfLoadedQuestFilter = HashSet(enabledQuestFilters)
                        setupQuestFilters(parentLayoutForRefresh)
                    }
                    Toast.makeText(requireContext(), "Filter '$filterName' applied", Toast.LENGTH_SHORT).show()
                    dialog.dismiss()
                }

                deleteButton.setOnClickListener {
                    showDeleteConfirmationDialog(filterName, filterType) {
                        if (filterType == "Rocket") {
                            enabledRocketFilters.clear()
                            enabledRocketFilters.addAll(filterPreferences.getEnabledCharacters())
                            originalSettingsOfLoadedRocketFilter = null
                            setupRocketFilters(parentLayoutForRefresh)
                        } else {
                            enabledQuestFilters.clear()
                            enabledQuestFilters.addAll(filterPreferences.getEnabledQuestFilters())
                            originalSettingsOfLoadedQuestFilter = null
                            setupQuestFilters(parentLayoutForRefresh)
                        }
                        updateDialogContent()
                    }
                }
                listContainer.addView(itemView)
            }
        }

        updateDialogContent()
        dialog.show()
    }

    private fun showDeleteConfirmationDialog(
        filterName: String,
        filterType: String,
        onDeleted: () -> Unit
    ) {
        val builder = AlertDialog.Builder(requireContext())
        val inflater = requireActivity().layoutInflater
        val dialogView = inflater.inflate(R.layout.dialog_delete_filter, null)

        val filterNameDisplay = dialogView.findViewById<TextView>(R.id.filterNameDisplay)
        val cancelButton = dialogView.findViewById<Button>(R.id.cancelDeleteButton)
        val deleteButton = dialogView.findViewById<Button>(R.id.confirmDeleteButton)

        filterNameDisplay.text = filterName

        builder.setView(dialogView)
        val dialog = builder.create()

        // Set up button listeners
        cancelButton.setOnClickListener {
            dialog.dismiss()
        }

        deleteButton.setOnClickListener {
            val wasActiveRocket = filterType == "Rocket" && filterName == filterPreferences.getActiveRocketFilter()
            val wasActiveQuest = filterType == "Quest" && filterName == filterPreferences.getActiveQuestFilter()

            filterPreferences.deleteFilter(filterName, filterType)

            if (wasActiveRocket) {
                originalSettingsOfLoadedRocketFilter = null
            }
            if (wasActiveQuest) {
                originalSettingsOfLoadedQuestFilter = null
            }

            Toast.makeText(requireContext(), "Filter '$filterName' deleted", Toast.LENGTH_SHORT).show()
            dialog.dismiss()
            onDeleted()
        }

        dialog.show()
    }

    private fun addToggleAllButton(parent: LinearLayout, filterType: String) {
        val toggleButton = MaterialButton(requireContext()).apply {
            text = "Toggle All $filterType Filters"
            setPadding(16, 8, 16, 8)
            setOnClickListener {
                when (filterType) {
                    "Rocket" -> {
                        val allSelected = DataMappings.characterNamesMap.keys.all { it in enabledRocketFilters }
                        if (allSelected) {
                            enabledRocketFilters.clear()
                        } else {
                            enabledRocketFilters.clear()
                            enabledRocketFilters.addAll(DataMappings.characterNamesMap.keys)
                        }
                        filterPreferences.saveEnabledCharacters(enabledRocketFilters)
                        setupRocketFilters(parent)
                    }
                    "Quest" -> {
                        val filtersJson = questPrefs.getString("quest_api_filters", null)
                        if (filtersJson != null) {
                            val filtersFromApi = Gson().fromJson(filtersJson, Quests.Filters::class.java)
                            val allPossibleQuestFilters = mutableSetOf<String>()
                            listOfNotNull(
                                filtersFromApi.t3 to "Stardust",
                                filtersFromApi.t4 to "Pokmon Candy",
                                filtersFromApi.t12 to "Mega Energy",
                                filtersFromApi.t7 to "Pokmon Encounter",
                                filtersFromApi.t2 to "Item"
                            ).forEach { (list, section) ->
                                list.forEach { rawValue ->
                                    allPossibleQuestFilters.add(buildQuestFilterString(section, rawValue))
                                }
                            }

                            if (allPossibleQuestFilters.isNotEmpty()) {
                                val allCurrentlySelected = enabledQuestFilters.containsAll(allPossibleQuestFilters) &&
                                        enabledQuestFilters.size == allPossibleQuestFilters.size

                                if (allCurrentlySelected) {
                                    enabledQuestFilters.clear()
                                } else {
                                    enabledQuestFilters.clear()
                                    enabledQuestFilters.addAll(allPossibleQuestFilters)
                                }
                                filterPreferences.saveEnabledQuestFilters(enabledQuestFilters)
                                setupQuestFilters(parent)
                            }
                        }
                    }
                }
            }
        }
        parent.addView(toggleButton, 0)
    }

    private fun setupQuestFilters(parent: LinearLayout) {
        parent.removeAllViews()
        addResetButton(parent, "Quest")
        addToggleAllButton(parent, "Quest")
        addSectionHeader(parent, "Quest Filters")
        addSelectFilterButton(parent, "Quest")
        if(currentFilterType == "Quest") updateCurrentQuestFilter()

        val filtersJson = questPrefs.getString("quest_api_filters", null)
        if (filtersJson != null) {
            val filters = Gson().fromJson(filtersJson, Quests.Filters::class.java)

            val spindaFormsMap: Map<String, Int> = questsViewModel.spindaFormsLiveData.value ?: emptyMap()
            Log.d("FilterFragment", "All available Spinda forms (cached): ${spindaFormsMap.keys}")

            addFilterSection(parent, "Stardust", filters.t3)
            addFilterSection(parent, "Pokmon Candy", filters.t4)
            addFilterSection(parent, "Mega Energy", filters.t12)

            addFilterSection(parent, "Pokmon Encounter", filters.t7, spindaFormsMap)

            addFilterSection(parent, "Item", filters.t2)
        } else {
            questsViewModel.fetchQuests()
            addSectionHeader(parent, "Please open quests tab to update data (or data loading)")
        }
    }



    private fun addSectionHeader(parent: LinearLayout, text: String) {
        TextView(context).apply {
            this.text = text
            textSize = 18f
            setPadding(16)
            parent.addView(this)
        }
    }

    private fun addCheckBox(
        parent: LinearLayout,
        text: String,
        id: Int,
        enabledSet: MutableSet<Int>,
        onCheckedChangeExternal: (Boolean) -> Unit
    ) {
        CheckBox(context).apply {
            this.text = text
            isChecked = id in enabledSet
            setPadding(32, 8, 16, 8)
            setOnCheckedChangeListener { _, checked ->
                if (checked) {
                    enabledSet.add(id)
                } else {
                    enabledSet.remove(id)
                }
                onCheckedChangeExternal(checked)
            }
            parent.addView(this)
        }
    }

    private fun addQuestCheckBox(
        parent: LinearLayout,
        text: String,
        id: String,
        enabledSet: MutableSet<String>,
        onCheckedChangeExternal: (Boolean) -> Unit
    ) {
        CheckBox(context).apply {
            this.text = text
            isChecked = id in enabledSet
            setPadding(32, 8, 16, 8)
            setOnCheckedChangeListener { _, checked ->
                if (checked) {
                    enabledSet.add(id)
                } else {
                    enabledSet.remove(id)
                }
                onCheckedChangeExternal(checked)
            }
            parent.addView(this)
        }
    }

    private fun buildQuestFilterString(section: String, rawValue: String): String {
        return when (section) {
            "Stardust" -> "3,$rawValue,0"
            "Mega Energy" -> "12,0,$rawValue"
            "Pokmon Encounter" -> "7,0,$rawValue"
            "Item" -> "2,0,$rawValue"
            "Pokmon Candy" -> "4,0,$rawValue"
            else -> rawValue
        }
    }

    private fun addFilterSection(
        parent: LinearLayout,
        sectionName: String,
        filterList: List<String>
    ) {
        addSectionHeader(parent, sectionName)

        if (filterList.isEmpty()) {
            TextView(context).apply {
                text = "None available for $sectionName"
                setPadding(16) // consistent 16dp padding around text
                parent.addView(this)
            }
        } else {
            // Sort differently if its one of the special categories; else alphabetical
            val sortedList = when (sectionName) {
                "Pokmon Encounter", "Mega Energy", "Pokmon Candy" ->
                    filterList.sortedBy { DataMappings.pokemonEncounterMapNew[it] ?: it }
                else ->
                    filterList.sorted()
            }

            sortedList.forEach { rawValue ->
                val displayText = when (sectionName) {
                    "Pokmon Encounter" -> DataMappings.pokemonEncounterMapNew[rawValue]
                        ?: "ID: $rawValue"
                    "Item"              -> DataMappings.itemMap["item$rawValue"]
                        ?: "Item ID: $rawValue"
                    "Mega Energy"       -> DataMappings.pokemonEncounterMapNew[rawValue]
                        ?: "Energy for ID: $rawValue"
                    "Pokmon Candy"     -> DataMappings.pokemonEncounterMapNew[rawValue]
                        ?: "Candy for ID: $rawValue"
                    else                -> rawValue
                }

                val compositeValue = buildQuestFilterString(sectionName, rawValue)

                // Only if it's **not** Spinda (ID 327) do we use a plain checkbox
                if (!(sectionName == "Pokmon Encounter" && rawValue == "327")) {
                    addQuestCheckBox(
                        parent,
                        displayText,
                        compositeValue,
                        enabledQuestFilters
                    ) {
                        filterPreferences.saveEnabledQuestFilters(enabledQuestFilters)
                    }
                }
            }
        }
    }
    private fun addFilterSection(
        parent: LinearLayout,
        sectionName: String,
        filterList: List<String>,
        spindaFormsMap: Map<String, Int>
    ) {
        addSectionHeader(parent, sectionName)

        if (filterList.isEmpty()) {
            TextView(context).apply {
                text = "None available for $sectionName"
                setPadding(16)
                parent.addView(this)
            }
        } else {
            val sortedList = filterList.sortedBy { DataMappings.pokemonEncounterMapNew[it] ?: it }

            sortedList.forEach { rawValue ->
                val displayText = DataMappings.pokemonEncounterMapNew[rawValue] ?: "ID: $rawValue"
                val compositeValue = buildQuestFilterString(sectionName, rawValue)

                if (rawValue == "327") {
                    addSpindaFilterWithForms(
                        parent,
                        displayText,
                        compositeValue,
                        spindaFormsMap
                    )
                } else {
                    addQuestCheckBox(
                        parent,
                        displayText,
                        compositeValue,
                        enabledQuestFilters
                    ) {
                        filterPreferences.saveEnabledQuestFilters(enabledQuestFilters)
                    }
                }
            }
        }
    }




    private fun getAvailableSpindaForms(): Map<String, Int> {
        val spindaForms = mutableMapOf<String, Int>()
        val quests = CurrentQuestData.currentQuests ?: emptyList() // Ensure null safety
        val spindaQuests = quests.filter { quest ->
            quest.rewardsIds.split(",").any { it == "327" }
        }
        spindaQuests.forEach { quest ->
            val formPattern = "\\((\\d+)\\)".toRegex()
            val matches = formPattern.findAll(quest.rewardsString)
            matches.forEach { matchResult ->
                val formNumber = matchResult.groupValues[1]
                val formKey = "spinda_form_$formNumber"
                spindaForms[formKey] = spindaForms.getOrDefault(formKey, 0) + 1
            }
        }
        Log.d("FilterFragment", "getAvailableSpindaForms returning: ${spindaForms.keys}")
        return spindaForms
    }

    private fun addSpindaFilterWithForms(
        parent: LinearLayout,
        displayText: String,
        baseCompositeValue: String,
        spindaFormsMap: Map<String, Int>
    ) {
        // Container for the entire Spinda block
        val spindaContainer = LinearLayout(requireContext()).apply {
            orientation = LinearLayout.VERTICAL
            layoutParams = LinearLayout.LayoutParams(
                ViewGroup.LayoutParams.MATCH_PARENT,
                ViewGroup.LayoutParams.WRAP_CONTENT
            )
        }

        // Top row: main Spinda checkbox + spacer + expand/collapse button
        val topRow = LinearLayout(requireContext()).apply {
            orientation = LinearLayout.HORIZONTAL
            gravity = Gravity.CENTER_VERTICAL
            layoutParams = LinearLayout.LayoutParams(
                ViewGroup.LayoutParams.MATCH_PARENT,
                ViewGroup.LayoutParams.WRAP_CONTENT
            )
        }

        // We need to access formCheckboxes inside the main checkbox listener,
        // so we declare the list before the main checkbox is defined.
        val formCheckboxes = mutableListOf<CheckBox>()

        // Main Spinda checkbox itself
        val mainSpindaCheckbox = CheckBox(requireContext()).apply {
            text = displayText
            isChecked = baseCompositeValue in enabledQuestFilters
            setPadding(32, 8, 16, 8)
            layoutParams = LinearLayout.LayoutParams(
                ViewGroup.LayoutParams.WRAP_CONTENT,
                ViewGroup.LayoutParams.WRAP_CONTENT
            )

            setOnCheckedChangeListener { _, isChecked ->
                if (isChecked) {
                    enabledQuestFilters.add(baseCompositeValue)
                    formCheckboxes.forEach { it.isChecked = true }

                } else {
                    //  If the main toggle is turned off...
                    enabledQuestFilters.remove(baseCompositeValue)

                    // ...uncheck all specific form checkboxes in the UI...
                    formCheckboxes.forEach { it.isChecked = false }

                    // ...and clear them from saved preferences in the backend.
                    filterPreferences.clearEnabledSpindaForms()
                }
                filterPreferences.saveEnabledQuestFilters(enabledQuestFilters)

                // Enable or disable all child checkboxes based on the parent's state.
                formCheckboxes.forEach { it.isEnabled = isChecked }
            }
        }

        // Spacer
        val spacer = View(requireContext()).apply {
            layoutParams = LinearLayout.LayoutParams(0, 0, 1f)
        }

        // Expand/Collapse button
        val expandButton = MaterialButton(requireContext()).apply {
            text = if (spindaFormsMap.isNotEmpty()) "" else ""
            isEnabled = spindaFormsMap.isNotEmpty()
            backgroundTintList = ColorStateList.valueOf(Color.TRANSPARENT)
            val onSurface = TypedValue().also {
                requireContext().theme.resolveAttribute(
                    com.google.android.material.R.attr.colorOnSurface, it, true
                )
            }.data
            setTextColor(onSurface)
            elevation = 0f
            minimumWidth = 0
            minWidth = 0
            setPadding(8.dpToPx(), 4.dpToPx(), 8.dpToPx(), 4.dpToPx())
            layoutParams = LinearLayout.LayoutParams(
                ViewGroup.LayoutParams.WRAP_CONTENT,
                ViewGroup.LayoutParams.WRAP_CONTENT
            )
        }

        topRow.addView(mainSpindaCheckbox)
        topRow.addView(spacer)
        if (spindaFormsMap.isNotEmpty()) {
            topRow.addView(expandButton)
        }

        // Container for specificform checkboxes (hidden initially)
        val formsContainer = LinearLayout(requireContext()).apply {
            orientation = LinearLayout.VERTICAL
            visibility = View.GONE
            setPadding((32 + 16).dpToPx(), 0, 16.dpToPx(), 8.dpToPx())
        }

        // Populate one checkbox per formKey
        if (spindaFormsMap.isNotEmpty()) {
            val enabledSpecificForms = filterPreferences.getEnabledSpindaForms()
            spindaFormsMap.keys.sorted().forEach { formKey ->
                val formNumber = formKey.removePrefix("spinda_form_")
                val formLabel = "Form #$formNumber"

                val formCheckbox = CheckBox(requireContext()).apply {
                    text = formLabel
                    isChecked = formKey in enabledSpecificForms

                    //  Child checkboxes are only enabled if the main checkbox is checked.
                    // This sets the initial state correctly on view creation.
                    isEnabled = mainSpindaCheckbox.isChecked

                    setPadding(0, 8.dpToPx(), 0, 8.dpToPx())
                    layoutParams = LinearLayout.LayoutParams(
                        ViewGroup.LayoutParams.WRAP_CONTENT,
                        ViewGroup.LayoutParams.WRAP_CONTENT
                    )

                    setOnCheckedChangeListener { _, isChecked ->
                        updateSpindaFormSelection(formKey, isChecked)
                    }
                }

                formCheckboxes.add(formCheckbox)
                formsContainer.addView(formCheckbox)
            }

            // Expand/Collapse logic
            var isExpanded = false
            expandButton.setOnClickListener {
                isExpanded = !isExpanded
                formsContainer.visibility = if (isExpanded) View.VISIBLE else View.GONE
                expandButton.text = if (isExpanded) "" else ""
            }
        }

        // Assemble into parent
        spindaContainer.addView(topRow)
        spindaContainer.addView(formsContainer)
        parent.addView(spindaContainer)
    }





    private fun Int.dpToPx(): Int {
        return (this * requireContext().resources.displayMetrics.density).toInt()
    }

    private fun updateSpindaFormSelection(formKey: String, isChecked: Boolean) {
        val currentForms = filterPreferences.getEnabledSpindaForms().toMutableSet()
        if (isChecked) {
            currentForms.add(formKey)
        } else {
            currentForms.remove(formKey)
        }
        filterPreferences.saveEnabledSpindaForms(currentForms)
        Log.d("FilterFragmentSpinda", "Updated specific Spinda forms: $currentForms")
    }



    override fun onResume() {
        super.onResume()
        if (::questLayout.isInitialized) {
            setupQuestFilters(questLayout)
        }
        if (::rocketLayoutGlobal.isInitialized) {
            setupRocketFilters(rocketLayoutGlobal)
        }
    }
}

package com.mints.projectgammatwo.ui

import android.app.AlertDialog
import android.content.Context
import android.content.SharedPreferences
import android.content.res.ColorStateList
import android.graphics.Color
